import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import io from 'socket.io-client';

const socket = io('http://localhost:3000'); // 소켓 연결

const App = () => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({});

  // 버튼 클릭 핸들러 - 서버로 데이터 요청
  const fetchData = () => {
    socket.emit('getData'); // 서버에 데이터 요청 이벤트 전송
  };

  useEffect(() => {
    // 서버로부터 데이터 수신
    socket.on('Vehicle_Info_Web', (receivedData) => {
      console.log(receivedData);
      setData(receivedData);

      const chart = {
        labels: receivedData.map((item, index) => index + 1),
        datasets: [
          {
            label: 'Data',
            data: receivedData.map((item) => item.value),
            fill: false,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1,
          },
        ],
      };
      setChartData(chart);
    });

    // Clean-up 함수: 컴포넌트가 언마운트될 때 소켓 이벤트 핸들러 제거
    return () => {
      socket.off('Vehicle_Info_Web');
    };
  }, []); // 빈 배열을 넘겨 최초 렌더링 시에만 useEffect 호출

  // 서버로부터 데이터 수신
  socket.on('Vehicle_Info_Web', (receivedData) => {
    console.log(receivedData);
    setData(receivedData);

    const chart = {
      labels: receivedData.map((item, index) => index + 1),
      datasets: [
        {
          label: 'Data',
          data: receivedData.map((item) => item.value),
          fill: false,
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1,
        },
      ],
    };
    setChartData(chart);
  });

  return (
    <div>
      <button onClick={fetchData}>데이터 가져오기</button>
      <div>
        {chartData && <Line data={chartData} />}
      </div>
    </div>
  );
};

export default App;
